import React from 'react';
import '../TodoList.css';

export default function BookCard({ todo, onMarkCompleted, onDelete, onRemoveFromCompleted, filter, onViewDetails }) {
  return (
    <div className={`book-card ${todo.completed ? 'completed' : ''}`} onMouseLeave={() => {}}>
      <img
        src={todo.image || process.env.PUBLIC_URL + '/no-image.jpg'}
        alt={todo.text}
        className="book-cover"
        onClick={() => onViewDetails && onViewDetails(todo)}
        style={{ cursor: 'pointer' }}
      />
      <div className="book-info">
        <h3 onClick={() => onViewDetails && onViewDetails(todo)} style={{ cursor: 'pointer' }}>{todo.text}</h3>
        <p>{todo.category || 'Fiction'}</p>
      </div>

      <div className="ellipsis-container">
        <button className="ellipsis-btn">\u22ef</button>
      </div>
    </div>
  );
}
